x = [0] * 100
y = [0] * 100
x[0] = 0
y[0] = 1
for i in range(1, 100):
  x[i] = x[i-1] + 0.2
  if x[i] > 1:
    break
  k1 = 0.2 * (x[i-1] + y[i-1])
  k2 = 0.2 * (x[i-1] + 0.1 + y[i-1] + k1/2)
  k3 = 0.2 * (x[i-1] + 0.1 + y[i-1] + k2/2)
  
  k4 = 0.2 * (x[i-1] + 0.2 + y[i-1] + k3)
  y[i] = y[i-1] + (k1 + 2 * k2 + 2 * k3 + k4) / 6
  print(i, ':', y[i])